package firstjavaproject;

public class Swapping {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 10;
		int b = 20;
		System.out.println("a:"+a);
		System.out.println("b:"+b);
		int swap;
		swap = a;
		a = b;
		b = swap;
		System.out.println("After Swapping");
		System.out.println("a:"+a);
		System.out.println("b:"+b);
	}

}
